import pandas as pdimport numpy as npfrom sklearn.model_selection import train_test_split# %%def separate_data(dict_input: dict) -> dict:        dict_input['Data'] = dict_input['Data'].join(        pd.get_dummies(            dict_input['Data']['ocean_proximity'])) \        .drop(columns=['ocean_proximity'])        dict_input['Data']['bedroom_ratio'] = dict_input['Data']['total_bedrooms'] / dict_input['Data']['total_rooms']        dict_input['Data']['household_rooms'] = dict_input['Data']['total_rooms'] / dict_input['Data']['households']        # Shape the pd.Series. +1 to prevent zero values    dict_input['Data']['total_rooms'] = np.log(dict_input['Data']['total_rooms'] + 1)        dict_input['Data']['total_bedrooms'] = np.log(dict_input['Data']['total_bedrooms'] + 1)        dict_input['Data']['population'] = np.log(dict_input['Data']['population'] + 1)        dict_input['Data']['households'] = np.log(dict_input['Data']['households'] + 1)        # Create x and y    x = dict_input['Data'].drop(columns=['median_house_value'])        y = dict_input['Data']['median_house_value']        # Split the data    (dict_input['x_train'],     dict_input['x_test'],     dict_input['y_train'],     dict_input['y_test']) = train_test_split(x, y, test_size=0.2)        dict_input['train_data'] = dict_input['x_train'].join(        dict_input['y_train'])        return dict_input